import React from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";

const CampusView = (props) => {
  const { campus, deleteCampus, deleteStudent } = props;

  // Check if the campus has any students
  const hasStudents = campus.students && campus.students.length > 0;

  return (
    <div>
      <h1>{campus.name}</h1>
      <p>{campus.address}</p>
      <p>{campus.description}</p>

      {/* Display message if no students are enrolled */}
      {!hasStudents && <p>No students enrolled at this campus yet.</p>}

      {/* List students if available */}
      {hasStudents && campus.students.map((student) => {
        const name = `${student.firstname} ${student.lastname}`;
        return (
          <div key={student.id}>
            <Link to={`/student/${student.id}`}>
              <h2>{name}</h2>
            </Link>
            {/* Button to delete a student */}
            <button onClick={() => deleteStudent(student.id, campus.id)}>
              Delete Student
            </button>
          </div>
        );
      })}

      {/* Optionally, add actions to manage students */}
      <div>
        <Link to={`/add-student/${campus.id}`}>
          <button>Add New Student</button>
        </Link>
        {/* Add a button to edit campus details */}
        <Link to={`/edit-campus/${campus.id}`}>
          <button>Edit Campus</button>
        </Link>
        {/* Button to delete campus */}
        <button onClick={() => deleteCampus(campus.id)}>Delete Campus</button>
      </div>
    </div>
  );
};

// PropTypes for validation
CampusView.propTypes = {
  campus: PropTypes.shape({
    id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    address: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    students: PropTypes.arrayOf(
      PropTypes.shape({
        id: PropTypes.string.isRequired,
        firstname: PropTypes.string.isRequired,
        lastname: PropTypes.string.isRequired,
      })
    ).isRequired,
  }).isRequired,
  deleteCampus: PropTypes.func.isRequired,
  deleteStudent: PropTypes.func.isRequired,
};

export default CampusView;
