// src/store/reducers/campuses.js

import * as at from "../actions/actionTypes";  // Import Action Types ("at" keyword for Action Type)

// REDUCER:
const allCampuses = (state = [], action) => {  // Default Initial State is an empty array
  switch (action.type) {
    case at.FETCH_ALL_CAMPUSES:
      return action.payload;  // When action type matches, the payload is returned as the new state
    default:
      return state;  // If no recognized action type, return the current state unchanged
  }
};

export default allCampuses;
